    private void submitButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitButtonActionPerformed

        databaseConnection con = new databaseConnection();
        Connection c = con.connect();
        String firstName,lastName, phoneNumber, gender = null, dateOfBirth, address, nid, acNumber, password;
        firstName = firstNameField.getText();
        lastName = lastNameField.getText();
        phoneNumber = phoneNumberField.getText();
        if(rbtnmale.isSelected()){
            gender="Male";
        }
        else if(rbtnfemale.isSelected()){
            gender = "Female";
        }
        dateOfBirth = dobField.getText();
        address = addressField.getText();
        nid = nidField.getText();
        acNumber = acField.getText();
        password = passwordField.getText();
        if(validateFields()){
        String sql = "insert into Bank (First_Name, Last_Name, Phone_Number, Gender, Date_Of_Birth, Address, NID, AC, Password) values(?, ?, ?, ?, ?, ?, ?, ?, ?);";
        try{
            try (PreparedStatement pst = c.prepareStatement(sql)) {
                pst.setString(1, firstName);
                pst.setString(2, lastName);
                pst.setString(3, phoneNumber);
                pst.setString(4, gender);
                pst.setString(5, dateOfBirth);
                pst.setString(6, address);
                pst.setString(7, nid);
                pst.setString(8, acNumber);
                pst.setString(9, password);
                pst.executeUpdate();
                JOptionPane.showMessageDialog(null, "You Have Succesfully create your account. now please Login...");
            }
            c.close();
            
        }
        catch(SQLException e){
            System.out.println(e);
            
        }
       }
    }//GEN-LAST:event_submitButtonActionPerformed

