    private boolean validateFields(){
        
        if(firstNameField.getText().isEmpty() | lastNameField.getText(). isEmpty() | phoneNumberField.getText(). isEmpty() | addressField.getText(). isEmpty() | acField.getText(). isEmpty() | dobField.getText(). isEmpty() | retypePasswordField.getText(). isEmpty() | passwordField.getText(). isEmpty()){
            JOptionPane.showMessageDialog(null, "Fields can't be empty");
            return false;
        }
        return true;
        
    }

